import React from "react";
import { Container } from "react-bootstrap";
import { getCustomerOrders } from "../../kustomer.api";
import SideBar from "../app/components/SideBar";
import { Customer } from "../customer/Customer";
import { CustomerProvider } from "../customer/CustomerContext";
import { CustomerSearch } from "../customer/CustomerSearch";

export const ReportForm = () => {
  const handleCustomerSelect = (
    event: React.MouseEvent<HTMLDivElement, MouseEvent>,
    customerData: ICustomerData
  ) => {
    if (customerData) {
      getCustomerOrders(customerData.data.relationships.orders.links.self);
    }
  };

  return (
    <>
      <CustomerProvider>
        <Container style={{ height: "90%" }} className="z-depth-1">
          <CustomerSearch />
          <Customer onClick={handleCustomerSelect} />
        </Container>
        <SideBar />
      </CustomerProvider>
    </>
  );
};
