import React from "react";
import Form from "react-bootstrap/Form";

type RefReturn =
  | string
  | ((instance: HTMLInputElement | null) => void)
  | React.RefObject<HTMLInputElement>
  | null
  | undefined;

type RefundAmountProps = {
  name: string;
};

type InputProps = React.DetailedHTMLProps<
  React.InputHTMLAttributes<HTMLInputElement>,
  HTMLInputElement
> & {
  props: RefundAmountProps;
  register: ({ required }: { required?: boolean }) => RefReturn};

const RefundAmount: React.FC<InputProps> = ({props, register, required }) => (
    <Form.Group>
      {console.log("in the refund amount component")}
      <Form.Label>Refund Amount (Solidus)</Form.Label>
      <input
        name={props.name}
        placeholder="Refund amount"
        ref={register({ required })}
      ></input>
    </Form.Group>
);

export default RefundAmount;
