import React, { FormEvent } from "react";
import {Form, Button} from "react-bootstrap";
import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";


interface FormInputs {
  purchaseReceived: string
  orderNumber: string
  vendor: string
  skuNumber: string
  damageLevel: string
}

const DDForm = (props:any) => {
  const { register, handleSubmit, errors } = useForm<FormInputs>();

  const onSubmit = (data: any) => {
    if (action !== undefined ) {
      action(data);
    }
    console.log(data);
  };

  return (
    <Form onSubmit={handleSubmit(onSubmit)}>
      <h2>Damaged/Defective Form</h2>
      {/* Purchase Received */}
      <Form.Group>
        <Form.Label>Purchase Received</Form.Label>
        <Form.Control
          name="purchaseReceived"
          as="select"
          ref={register({ required: true })}
        >
          <option></option>
          <option>Yes</option>
          <option>No</option>
        </Form.Control>
        {errors.purchaseReceived && "receipt status is required"}
      </Form.Group>

      {/* Order # */}
      <Form.Group>
        <Form.Label>Order #</Form.Label>
        <Form.Control
          name="orderNumber"
          placeholder="Order #"
          ref={register({ required: true })}
        ></Form.Control>
        {errors.orderNumber && "Order # is required"}
      </Form.Group>

      {/* Vendor */}
      <Form.Group>
        <Form.Label>Vendor</Form.Label>
        <Form.Control
          name="vendor"
          as="select"
          defaultValue="Choose Vendor..."
          ref={register({ required: true })}
        >
          <option></option>
          {vendorList.map((vendor) => (
            <option key={vendor}>{vendor}</option>
          ))}
        </Form.Control>
        {errors.vendor && "Vendor required"}
      </Form.Group>

      {/* SKU Number */}
      <Form.Group>
        <Form.Label>SKU #</Form.Label>
        <Form.Control
          name="skuNumber"
          placeholder="SKU #"
          ref={register({ required: true })}
        ></Form.Control>
        {errors.skuNumber && "SKU # is required"}
      </Form.Group>

      {/* Damage Level */}
      <Form.Group>
        <Form.Label>Damge Level</Form.Label>
        <Form.Control
          name="damageLevel"
          as="select"
          defaultValue="Damage Level..."
          ref={register({ required: true })}
        >
          <option></option>
          {damageLevels.map((level) => (
            <option key={level}>{level}</option>
          ))}
        </Form.Control>
        {errors.damageLevel && "Please select damage level"}
      </Form.Group>

      {/* Image Upload */}
      <Button type="submit">Submit</Button>
    </Form>
  );
};

export default DDForm;

const vendorList = ["vendor1", "vendor2", "vendor3", "vendor4", "vendor 5"];
const damageLevels = ["Level 1", "Level 2", "Level 3"];
