import React, { FormEvent } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";

const DDForm = () => {
  const { register, handleSubmit } = useForm();

  const onSubmit = (values: any) => {
    console.log(values);
  };
  return (
    <Form onSubmit={handleSubmit(onSubmit)}>
      {/* Purchase Received */}
      <Form.Control as="select" ref={register({ required: true })}>
        <option>Yes</option>
        <option>No</option>
      </Form.Control>

      {/* Order # */}
      <Form.Control
        placeholder="Order #"
        ref={register({ required: true })}
      ></Form.Control>

      {/* Vendor */}
      <Form.Control
        as="select"
        defaultValue="Choose Vendor..."
        ref={register({ required: true })}
      >
        {/* Vendor list should be populated programatically */}
        <option>Choose Vendor...</option>
        {vendorList.map((vendor) => (
          <option key={vendor}>{vendor}</option>
        ))}
      </Form.Control>

      <Form.Control
        placeholder="SKU #"
        ref={register({ required: true })}
      ></Form.Control>
      <Form.Control
        as="select"
        defaultValue="Damage Level..."
        ref={register({ required: true })}
        >
        <option>Damage Level...</option>
        {damageLevels.map((level) => (
          <option key={level}>{level}</option>
        ))}
      </Form.Control>

      <Button type="submit">Submit</Button>
    </Form>
  );
};

export default DDForm;

const vendorList = ["vendor1", "vendor2", "vendor3", "vendor4", "vendor 5"];
const damageLevels = ["Level 1", "Level 2", "Level 3"]
