import React, { useState } from 'react';
import Col from 'react-bootstrap/Col'
import Row from 'react-bootstrap/Row'

type CustomerCardProps = {
  customerData: any
}


export const CustomerCard = (props: CustomerCardProps) => {
  const [isHovered, setHovered] = useState(false)

  const name = props.customerData.item.attributes.displayName
  const emails = props.customerData.item.attributes.emails[0]
  const phones = props.customerData.item.attributes.phones[0]
  let email = ""
  let phone = ""
  if (emails !== undefined) {
    email = emails.email
  }

  if (phones !== undefined) {
    phone = phones.phone
  }

  const cardStyles = {
    backgroundColor: "white",
    color: "black",
    border: "1px solid gray",
    borderRadius: "5px",
    padding: "10px",
    margin: "10px"
  }

  const handleMouseEnter = (event: React.MouseEvent<HTMLDivElement, MouseEvent>) => {
    console.log(event)
    setHovered(true)
  }

  const handleMouseLeave = (event: React.MouseEvent<HTMLDivElement, MouseEvent>) => {
    console.log(event)
    setHovered(false)
  }

  return(
    <div style={cardStyles} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
      <h6>{name}</h6>
      <Row>
        <Col><p>{email}</p></Col>
        <Col><p>{phone}</p></Col>
      </Row>

    </div>
  )
}
