import axios, { AxiosResponse } from 'axios'

const baseUrl: string = "http://localhost:4000"

export const addDamagedDefect = async(
  formData: IDamagedDefect
): Promise<AxiosResponse<ApiDataType>> => {
  try {
    const damagedDefect: Omit<IDamagedDefect, "_id"> = {
      received: formData.purchaseReceived,
      order_number: formData.orderNumber,
      vendor: formData.vendor,
      sku_number: formData.skuNumber,
      damage_level: formData.damageLevel,
      discount_refund_replace: formData.offerDiscount,
      narvar_return_info: formData.narvarReturn,
      item_amount: formData.itemAmount,
      file_case_description: formData.damageDescription,
      action_needed: formData.actionNeeded
    }
    const saveDamagedDefect: AxiosResponse<ApiDataType> = await axios.post(
      baseUrl + "/submit-damaged-defect",
      damagedDefect
    )
    return saveDamagedDefect
  } catch (error) {
    throw new Error(error)
  }
}
