import React from 'react';
import { ModalBody } from 'react-bootstrap';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row'
import { StringDecoder } from 'string_decoder';

type SubmitConfirmationProps = {
  show: boolean
  onHide: () => void
  data: any
}

type ElementObject = {
  key: string,
  value: string
}

const SubmitConfirmation = (props: SubmitConfirmationProps) => {

  const formatData = (data:any) => {
    if (data !== undefined) {
      const dataEntries = Object.entries(data);
      for (const entry in dataEntries) {
        const thisKey = entry[0]
        let newKey = ""
        if (thisKey === "purchaseReceived") {
          newKey = "Purchase Received?: "
        }
      }
      return dataEntries;
    } else {
      return [];
    }
  }

  return(
    <Modal show={props.show} onHide={props.onHide}>
      <Modal.Header closeButton>
          <Modal.Title>Review Damage/Defect Submission</Modal.Title>
      </Modal.Header>
      <ModalBody>
        {formatData(props.data).map((field) => (

          <Row><h3>{field[0]}</h3>: <h3>{}</h3></Row>
        ))}
      </ModalBody>
      <Button type="submit">Submit</Button>
    </Modal>
  );
};

export default SubmitConfirmation;
