import { MDBBtn, MDBContainer, MDBIcon, MDBInputGroup } from "mdbreact";
import React, { useState } from "react";

interface ICustomerSearchProps {}

export const CustomerSearch: React.FC = () => {
  const [searchValue, setSearchValue] = useState<string>("");

  const isValidEmail = (value: string) => {
    if (value) return true;
    return false;
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const el = e.target;
    const value = el.value;
    setSearchValue(value);
  };

  const handleSubmit = (
    e: React.SyntheticEvent<HTMLButtonElement, Event>
  ) => {};
  return (
    <>
      <MDBInputGroup
        style={{
          display: "flex",
          alignContent: "center",
        }}
        onChange={handleChange}
        type="email"
        hint="Search Customer Email"
        append={
          <MDBBtn
            type="submit"
            style={{ padding: 0, margin: 0, width: 50 }}
            outlined
            color="blue"
            onClick={handleSubmit}
          >
            <MDBIcon icon="fas fa-search" />
          </MDBBtn>
        }
      />
    </>
  );
};
