import React, { ChangeEvent } from 'react';
import Form from 'react-bootstrap/Form';

type OfferDiscountProps = {
  onChange: (event: ChangeEvent<any>) => void
}

type SelectProps = React.DetailedHTMLProps<
  React.SelectHTMLAttributes<HTMLSelectElement>,
  HTMLSelectElement
> & { options: OfferDiscountProps } & HTMLSelectElement;

const OfferDiscount = React.forwardRef<SelectProps, OfferDiscountProps>((props, ref) => (
  <Form.Control name="offerDiscount" as="select" onChange={props.onChange} ref={ref}>
    <option>Discount</option>
    <option>Refund</option>
    <option>Replacement</option>
  </Form.Control>
));

export default OfferDiscount;
