import React from "react";
import Form from "react-bootstrap/Form";

type RefundAmountProps = {
    name: string
}

type TextProps = React.DetailedHTMLProps<
React.TextareaHTMLAttributes<HTMLTextAreaElement>,
HTMLTextAreaElement> & { options: RefundAmountProps } & HTMLTextAreaElement

const RefundAmount = React.forwardRef<TextProps, RefundAmountProps>((props , ref) => (
  <Form.Group>
    <Form.Label>Refund Amount (Solidus)</Form.Label>
    <Form.Control
      name={props.name}
      type="text"
      placeholder="Refund amount"
      ref={ref}
    ></Form.Control>
  </Form.Group>
));

export default RefundAmount;
