import React from "react";
import {
  BrowserRouter as Router,
  Route,
  Link,
  useLocation,
} from "react-router-dom";
import { StateMachineProvider, createStore } from "little-state-machine";
import { DevTool } from "little-state-machine-devtools";
import LevelOne from "./DamageDefectForm/LevelOne";
import LevelTwo from "./DamageDefectForm/LevelTwo";
import LevelThree from "./DamageDefectForm/LevelThree";
import DamageDefectFormV2 from "./DamageDefectForm/DamageDefectFormV2";
import { Nav, Navbar } from "react-bootstrap";

const Forms = () => {
  const location = useLocation();
  return (
    <>
      <Navbar>
        <Nav.Link className={location.pathname === "/" ? "active" : ""}>Damage/Defective Form</Nav.Link>
        <Nav.Link>Another Form</Nav.Link>
      </Navbar>
    </>
  );
}

const FormContainer = () => {
  return (
    <div style={{ height: "100%", width: "100%", display: "flex" }}>
      <DamageDefectFormV2 />
      <div style={{ height: "100%", width: "70%" }}>Form guide</div>
    </div>
  );
};

export default FormContainer;
