import React, { ChangeEvent, useState } from 'react'
import Form from 'react-bootstrap/Form'
import { MDBInput, MDBCol, MDBFormInline, MDBIcon } from 'mdbreact'
// require("dotenv/config" )
const kustomerAPIKey = process.env.REACT_APP_KUSTOMER_API_KEY

console.log(process.env)
// console.log(kustomerAPIKey)
const xhr = new XMLHttpRequest();
// xhr.withCredentials = true;
const getAllCustomers = async () => {
  let proxyUrl: string = "https://cors-anywhere.herokuapp.com/"
  let requestUrl: string = "https://api.kustomerapp.com/v1/customers"
  // let requestUrl: string = "https://api.kustomerapp.com/v1/auth/tokens/current"

  xhr.open("GET", proxyUrl + requestUrl, true);
  xhr.setRequestHeader("authorization", `Bearer ${kustomerAPIKey}`);
  xhr.send();
  xhr.onreadystatechange = () => {
    let response = xhr.response
    console.log(response)
  }
};

export const CustomerInfo = () => {
  const [searchValue, setSearchValue] = useState<string>('')

  const customerInfoStyle = {
    borderRight: "2px solid gray",
    margin: "10px",
    height: "95%",
  }

  const handleSearchChange = (event: ChangeEvent<HTMLInputElement>) => {
    const el = event.target as HTMLInputElement
    const value = el.value
    // setSearchValue(el.value)
    console.log(value)
  }

  return (
    <div style={customerInfoStyle}>
      <MDBCol md="12">
        <h2>Customer Info</h2>
        <MDBFormInline className="md-form" style={{ display: "flex", alignContent: "center"}}>
          <MDBIcon icon="search"></MDBIcon>
          <input value={searchValue} className="form-control form-control-lg ml-3 w-75" type="text" placeholder="Search" aria-label="Search" onChange={handleSearchChange}></input>
        </MDBFormInline>
      </MDBCol>

      <div>

      </div>
      <button onClick={getAllCustomers}></button>
    </div>
  );
};
