import React, { useState, FormEvent } from 'react';
import Form from 'react-bootstrap/Form';

type DiscountToKeepProps = {
    selection?: string | null
    onChange?: (id: null | string) => void
}

const DiscountToKeep = (props: DiscountToKeepProps) => {
    const handleSelection = (event:FormEvent<HTMLDivElement>) => {
        const el = event.target as HTMLDivElement
        const id = el.getAttribute('id');
        props.onChange(id);
    }

    return (
        <Form.Group onChange={handleSelection}>
            <Form.Label>Discount To Keep</Form.Label>
            <Form.Check name="discountRefundRadios" type="radio" label="Discount To Keep" id="discountToKeep"></Form.Check>
            <Form.Check name="discountRefundRadios" type="radio" label="Refund" id="refund"></Form.Check>
        </Form.Group>

    )
}

export default DiscountToKeep;
