import React, { FormEvent, ChangeEvent } from "react";
import { Form, Button } from "react-bootstrap";
import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import LevelOneHead from "./LevelOne";
import LevelTwoHead from "./LevelTwo";
import LevelThreeHead from "./LevelThree";
import DiscountToKeep from "./FormComponents/DiscountToKeep";
import OfferDiscount from "./FormComponents/OfferDiscount";

interface FormInputs {
  purchaseReceived: string;
  orderNumber: string;
  vendor: string;
  skuNumber: string;
  damageLevel: string;
}

const DDForm = (props: any) => {
  const [level, setLevel] = useState();
  const [levelHeadElement, setLevelHeadElement] = useState<JSX.Element>();
  const [imageUploadElements, setImageUploadElements] = useState<
    Array<JSX.Element>
  >([]);
  const [offerDiscountElement, setOfferDiscountElement] = useState<JSX.Element>();
  const [refundReplaceElement, setRefundReplaceElement] = useState<JSX.Element>();
  const { register, handleSubmit, errors } = useForm<FormInputs>();

  const handleLevelChange = (event: ChangeEvent<any>) => {
    const el = event.target as HTMLFormElement;
    const value = el.value;
    setLevel(value);

    if (value === "Level 1") {
      setLevelHeadElement(<LevelOneHead></LevelOneHead>);
      setImageUploadElements([
        <Form.File key="image1" name="image1" ref={register} />,
      ]);
      setOfferDiscountElement(<OfferDiscount onChange={handleOfferDiscount}/>)


    } else if (value === "Level 2") {
      setLevelHeadElement(<LevelTwoHead></LevelTwoHead>);
      setImageUploadElements([
        <Form.File key="image1" name="image1" ref={register} />,
        <Form.File key="image2" name="image2" ref={register} />,
        <Form.File key="image3" name="image3" ref={register} />,
      ]);
      setOfferDiscountElement(<OfferDiscount onChange={handleOfferDiscount}/>)

    } else if (value === "Level 3") {
      setLevelHeadElement(<LevelThreeHead></LevelThreeHead>);
      setImageUploadElements([
        <Form.File key="image1" name="image1" ref={register} />,
        <Form.File key="image2" name="image2" ref={register} />,
        <Form.File key="image3" name="image3" ref={register} />,
      ]);
    }
  };

  const handleOfferDiscount = (event: ChangeEvent<any>) => {
    const el = event.target as HTMLFormElement;
    const value = el.value;
    console.log("Handling offer discount");
    console.log(value);
  }

  const onSubmit = (data: any) => {
    console.log(data);
  };

  return (
    <Form onSubmit={handleSubmit(onSubmit)}>
      <h2>Damaged/Defective Form</h2>
      {/* Purchase Received */}
      <Form.Group>
        <Form.Label>Purchase Received</Form.Label>
        <Form.Control
          name="purchaseReceived"
          as="select"
          ref={register({ required: true })}
        >
          <option></option>
          <option>Yes</option>
          <option>No</option>
        </Form.Control>
        {errors.purchaseReceived && "receipt status is required"}
      </Form.Group>

      {/* Order # */}
      <Form.Group>
        <Form.Label>Order #</Form.Label>
        <Form.Control
          name="orderNumber"
          placeholder="Order #"
          ref={register({ required: true })}
        ></Form.Control>
        {errors.orderNumber && "Order # is required"}
      </Form.Group>

      {/* Vendor */}
      <Form.Group>
        <Form.Label>Vendor</Form.Label>
        <Form.Control
          name="vendor"
          as="select"
          defaultValue="Choose Vendor..."
          ref={register({ required: true })}
        >
          <option></option>
          {vendorList.map((vendor) => (
            <option key={vendor}>{vendor}</option>
          ))}
        </Form.Control>
        {errors.vendor && "Vendor required"}
      </Form.Group>

      {/* SKU Number */}
      <Form.Group>
        <Form.Label>SKU #</Form.Label>
        <Form.Control
          name="skuNumber"
          placeholder="SKU #"
          ref={register({ required: true })}
        ></Form.Control>
        {errors.skuNumber && "SKU # is required"}
      </Form.Group>

      {/* Damage Level */}
      <Form.Group>
        <Form.Label>Damge Level</Form.Label>
        <Form.Control
          name="damageLevel"
          as="select"
          defaultValue="Damage Level..."
          ref={register({ required: true })}
          onChange={handleLevelChange}
          value={level}
        >
          <option></option>
          {damageLevels.map((level) => (
            <option key={level} value={level}>
              {level}
            </option>
          ))}
        </Form.Control>
        {errors.damageLevel && "Please select damage level"}
      </Form.Group>

      {/* Level Paths */}
      {levelHeadElement}
      {imageUploadElements}
      {offerDiscountElement}
      <Button type="submit">Submit</Button>
    </Form>
  );
};

export default DDForm;

const vendorList = ["vendor1", "vendor2", "vendor3", "vendor4", "vendor 5"];
const damageLevels = ["Level 1", "Level 2", "Level 3"];
