import React, { ChangeEvent, useState } from 'react'
import { MDBCol, MDBFormInline, MDBIcon } from 'mdbreact'
// require("dotenv/config" )
const kustomerAPIKey = process.env.REACT_APP_KUSTOMER_API_KEY

const xhr = new XMLHttpRequest();

const getCustomers = async (
  value: string
  ) => {
  let proxyUrl: string = "https://cors-anywhere.herokuapp.com/"
  let requestUrl: string = "https://api.kustomerapp.com/v1/customers/search"

  let data = JSON.stringify({
    "and": [],
    "or": [
      {
        "customer_name": {
          "equals": `${value}`
        }
      }
    ],
    "sort": [
      {
        "updatedAt": {
          "order": "asc"
        }
      }
    ],
    "queryContext": "customer"
  })
  let resData = {}
  try {
    xhr.open("POST", proxyUrl + requestUrl, true);
    xhr.setRequestHeader("authorization", `Bearer ${kustomerAPIKey}`);
    xhr.setRequestHeader("content-type", "application/json");
    xhr.send(data);

    xhr.onreadystatechange = () => {
      let response = xhr.responseText
      console.log(response)
      resData = JSON.parse(response)
      return resData
    }
  } catch (error) {
    throw new Error(error)
  }

};

export const CustomerInfo = () => {
  const [searchValue, setSearchValue] = useState<string>('')



  const customerInfoStyle = {
    borderRight: "2px solid gray",
    margin: "10px",
    height: "95%",
  }

  const handleSearchChange = (event: ChangeEvent<HTMLInputElement>) => {
    const el = event.target as HTMLInputElement
    const value = el.value
    setSearchValue(value)
    let results = getCustomers(value)
    console.log(results)
    // results = results.data

  }

  return (
    <div style={customerInfoStyle}>
      <MDBCol md="12">
        <h2>Customer Info</h2>
        <MDBFormInline className="md-form" style={{ display: "flex", alignContent: "center"}}>
          <MDBIcon icon="search"></MDBIcon>
          <input value={searchValue} className="form-control form-control-lg ml-3 w-75" type="text" placeholder="Search" aria-label="Search" onChange={handleSearchChange} onSubmit={handleSearchChange}></input>
        </MDBFormInline>
      </MDBCol>

      <div>
        Search Result Display
      </div>
    </div>
  );
};
