import React, { FormEvent } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";

const DDForm = () => {
  const { register, handleSubmit } = useForm();

  const onSubmit = (values: any) => {
    console.log(values);
  };
  return (
    <Form onSubmit={handleSubmit(onSubmit)}>
      {/* Purchase Received */}
      <Form.Control as="select" ref={register({ required: true })}>
        <option>Yes</option>
        <option>No</option>
      </Form.Control>

      {/* Order # */}
      <Form.Control
        placeholder="Order #"
        ref={register({ required: true })}
      ></Form.Control>

      {/* Vendor */}
      <Form.Control
        as="select"
        defaultValue="Choose Vendor..."
        ref={register({ required: true })}
      >
        {/* Vendor list should be populated programatically */}
        <option>Choose Vendor...</option>
        {vendorList.map((vendor) => (
          <option>{vendor}</option>
        ))}
      </Form.Control>

      <Form.Control
        placeholder="SKU #"
        ref={register({ required: true })}
      ></Form.Control>
      <Form.Group ref={register}>
        <Form.Check
          name="damageLevelRadios"
          type="radio"
          label="Level 1"
          id="lvl1"
        ></Form.Check>
        <Form.Check
          name="damageLevelRadios"
          type="radio"
          label="Level 2"
          id="lvl2"
        ></Form.Check>
        <Form.Check
          name="damageLevelRadios"
          type="radio"
          label="level 3"
          id="lvl3"
        ></Form.Check>
      </Form.Group>

      <Button type="submit">Submit</Button>
    </Form>
  );
};

export default DDForm;

const vendorList = ["vendor1", "vendor2", "vendor3", "vendor4", "vendor 5"];
