import React from 'react';
import { ModalBody } from 'react-bootstrap';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row'
import { StringDecoder } from 'string_decoder';

type SubmitConfirmationProps = {
  show: boolean
  onHide: () => void
  onSubmit: () => void
  data: any
}

type ElementObject = {
  key: string,
  value: string
}

const SubmitConfirmation = (props: SubmitConfirmationProps) => {

  const formatData = (data:any) => {
    if (data !== undefined) {
      const dataEntries = Object.entries(data);
      let outputEntries:any = []
      let index = 0;
      for (const entry in dataEntries) {
        const thisKey = entry[0]
        let newKey = ""
        // formats fields for display
        switch(thisKey) {
          case "purchaseReceived":
            newKey = "Purchase Received? ";
            break;
          case "orderNumber":
            newKey = "Order Number: ";
            break;
          case "vendor":
            newKey = "Vendor: ";
            break;
          case "skuNumber":
            newKey = "SKU #: ";
            break;
          case "damageLevel":
            newKey = "Damage Level: "
            break;
          case "image1":
            newKey = "Image 1: ";
            break;
          case "image2":
            newKey = "Image 2: ";
            break;
          case "image3":
            newKey = "Image 3: ";
            break;
          case "levelOneOfferDiscount" || "levelTwoOfferDiscount" || "levelThreeOfferDiscount":
            newKey = "Discount/Refund/Return: "
            break;
          case "levelOneRefund" || "levelTwoRefund" || "levelThreeRefund":
            newKey = "Refund Amount: ";
            break;
          case "levelTwoItemAmount" || "levelThreeItemAmount":
            newKey = "Item Amount: "
            break;
          case "damageDescription":
            newKey = "Damage Description: ";
            break;
          case "actionNeeded":
            newKey = "Action Needed: ";
            break;
          case "replacementOrder":
            newKey = "Replacement Order #";
            break;
        }
        outputEntries[index] = [newKey, entry[1]];
        index++;
      }
      console.log(outputEntries);
      return outputEntries;
    } else {
      return [];
    }
  }

  const handleSubmit = () => {
    props.onHide();
    props.onSubmit();
  }

  return(
    <Modal show={props.show} onHide={props.onHide}>
      <Modal.Header closeButton>
          <Modal.Title>Review Damage/Defect Submission</Modal.Title>
      </Modal.Header>
      <ModalBody>
        {formatData(props.data).map((field:any) => (
          <Row><h3>{field[0][0]}</h3>: <h3>{}</h3></Row>
        ))}
      </ModalBody>
      <Button type="submit" onClick={handleSubmit}>Submit</Button>
    </Modal>
  );
};

export default SubmitConfirmation;
