import React from "react";
import {
  BrowserRouter as Router,
  Route,
  Link,
  useLocation,
} from "react-router-dom";
import { StateMachineProvider, createStore } from "little-state-machine";
import { DevTool } from "little-state-machine-devtools";
import LevelOne from "./DamageDefectForm/LevelOne";
import LevelTwo from "./DamageDefectForm/LevelTwo";
import LevelThree from "./DamageDefectForm/LevelThree";
import DamageDefectFormV2 from "./DamageDefectForm/DamageDefectFormV2";
import { Nav, Navbar } from "react-bootstrap";
import DamageDefectForm from "./DamageDefectForm/DamageDefectForm";

const Forms = () => {
  const location = useLocation();
  return (
    <>
      <Navbar>
        <Nav.Link
          className={
            location.pathname === "/damagedefectiveform" ? "active" : ""
          }
        >
          <Link to="/damagedefectiveform">Damage/Defective Form</Link>
        </Nav.Link>
        <Nav.Link
          className={location.pathname === "/secondform" ? "active" : ""}
        >
          <Link to="/secondform">Another Form</Link>
        </Nav.Link>
      </Navbar>
      <Route exact path="/damagedefectiveform" component={DamageDefectFormV2}></Route>
      <Route path="/secondform" component={DamageDefectForm}></Route>
    </>
  );
};

const FormContainer = () => {
  return (
    <StateMachineProvider>
      {/* <DevTool /> */}
      <div style={{ height: "100%", width: "100%", display: "flex" }}>
        <Router>
          <Forms />
        </Router>
        <div style={{ height: "100%", width: "70%" }}>Form guide</div>
      </div>
    </StateMachineProvider>
  );
};

export default FormContainer;
